import * as Message from './../constants/Message'
import * as Types from './../constants/ActionType'


var initialState = Message.MSG_WELCOME;

const cart = (state = initialState, action) => {
    switch (action.type) {      
        case Types.CHANGE_MAASG:
            return action.message; 
        default:
            return state;
    }
}


export default cart;
